  }
  return (
    <div>
      <Header />
      <CreateArea anAdd={addItem} />
      {items.map((noteItem) => {
        return <Note title={noteItem.title} content={noteItem.content} />;
      })}
      <Footer />
    </div>
  );
}

export default App;
